      <v-card-text>
        <e-signature
          v-model="signature"
          label="Sign the box below"
          :show="true"
          :current-signature="null"
          :readonly="false"
          @change="newSignature"
        />
      </v-card-text>

      <v-divider></v-divider>

      <v-card-actions>
        <v-btn v-bind="$bind.btn" color="warning" @click="cancel">
          <v-icon left>mdi-close</v-icon>
          Close
        </v-btn>
        <v-spacer />
      </v-card-actions>
    </v-card>
  </v-dialog>
</template>

<script>
export default {
  data() {
    return {
      dialog: false,
      signature:
        "data:image/png;base64,",
    };
  },
  mounted() {
    this.open();
  },
  methods: {
    open() {
      this.dialog = true;

      return new Promise((resolve, reject) => {
        this.resolve = resolve;
        this.reject = reject;
      });
    },
    cancel() {
      this.dialog = false;

      this.resolve(false);
    },
    newSignature() {},
    saveSignature() {
      this.dialog = false;
      this.resolve(true);
      // alert("todo");
    },
  },
};
</script>

